import React from 'react';
import Button from '@material-ui/core/Button';
import AddIcon from '@material-ui/icons/Add';
import { useState } from 'react';
import Card from "./Card";
import shadows from '@material-ui/core/styles/shadows';
const Form = () => {
    const [InputValue, setInputValue] = useState({
        title:"",
        content: ""
    });
    const [NewInputItem, SetNewInputItem] = useState([]);
    
    const inputFun = (event) => {
        const {value, name} = event.target;

        setInputValue((prevValue) => {            
            return{
                ...prevValue,
                [name]: value
            }
        });
    }
    
    const addItem = () => {
        
        if(InputValue.title == "" || InputValue.content == ""){
            alert("Please enter title & contents");
        }else{
            SetNewInputItem((oldItem) => {
                return[...oldItem, InputValue]
            })
            setInputValue({
                title:"",
                content: ""
            })
        }
        
    }

    const DeleteItem = (id) => {
        SetNewInputItem((oldItem) => {
            return oldItem.filter((objOld, index) => {
                return index !== id;
            })
        })
    }

    return(
        <>
            <div className="container">
                <div className="row d-flex justify-content-center">
                    <div className="card col-12 col-md-4 mt-2">
                        <form className="card-body">
                            <div className="mb-3">                
                                <input type="text" value={InputValue.title} name="title" onChange={inputFun} className="form-control" id="title" placeholder="Title" aria-describedby="emailHelp" />
                            </div>
                            <div className="">
                                <textarea onChange={inputFun} value={InputValue.content} name="content" className="form-control" placeholder="Content" id="content"></textarea>
                            </div>
                            <Button type="button" onClick={addItem} className="btn btn_Add btn-sm btn-default float-right mt-2"><AddIcon /></Button>
                        </form>
                    </div>
                </div>
                <div className="row">                    
                    {NewInputItem.map((itemVal, index) => {
                        //console.log(index)
                        var colors = ['red', 'green', 'blue', 'orange', 'yellow'];
                        var divs = index;
                        for (var i = 0; i < divs.length; i++) {
                            var color = colors[i % colors.length];
                            divs[i].style.boxShadow = `2px 2px 2px ${color}`;
                            //console.log(divs)
                        };
                        return(
                            <Card
                                key={index}
                                id={index}
                                title={itemVal.title}
                                content={itemVal.content}
                                onSelect={DeleteItem}
                                background={"red"}
                                colors={colors[i]}
                            />
                        )
                    })}
                </div>
            </div>
        </>
    )
}
export default Form;
