import React from 'react';
import Button from '@material-ui/core/Button';
import AddIcon from '@material-ui/icons/Add';
import { useState } from 'react';

const Form = () => {
    const [inputItem, setInputItem] = useState(({
        title:"",
        content: ""
    }));
    const [ItemVal, setItemVal] = useState(({
        title:"",
        content: ""
    }));

    const inputFun = (event) => {
        setInputItem(event.target.value);
    }

    const addItem = (event) => {
        setItemVal(inputItem);
    }

    return(
        <>
            <h1>{ItemVal.title}</h1>
            <div className="container">
                <div className="row d-flex justify-content-center">
                    <div className="card col-12 col-md-4 mt-2">
                        <form className="card-body">
                            <div className="mb-3">                
                                <input type="text" value={inputFun.title} onChange={inputFun} className="form-control" id="title" placeholder="Title" aria-describedby="emailHelp" />
                            </div>
                            <div className="">
                                <textarea onChange={inputFun} value={inputFun.content} className="form-control" placeholder="Content" id="content"></textarea>
                            </div>
                            <Button type="button" onClick={addItem} className="btn btn_Add btn-sm btn-default float-right mt-2"><AddIcon /></Button>
                        </form>
                    </div>
                </div>
            </div>
        </>
    )
}
export default Form;
