import React from 'react';
import Button from '@material-ui/core/Button';
import AddIcon from '@material-ui/icons/Add';
import { useState } from 'react';

const Form = () => {

    const [inputVal ,setInputVal] = useState({
        title:"",
        content:""
    })

    const [inputItem, setInputItem] = useState();    
    
    const inputFun = (event) => {
        //setInputVal(event.target.value)
        setInputVal((prevValue) => {            
            return[
                ...prevValue,
                inputVal
            ]
        })
    }

    const addItem = () => {    
        // setInputItem((prevValue) => {            
        //     return{
        //         ...prevValue,
        //         inputItem
        //     }
        // });
        setInputItem();
        setInputVal("")
    }

    return(
        <>
            <h1>{inputItem.title}</h1>
            <div className="container">
                <div className="row d-flex justify-content-center">
                    <div className="card col-12 col-md-4 mt-2">
                        <form className="card-body">
                            <div className="mb-3">                
                                <input type="text" value={inputVal.title} name="title" onChange={inputFun} className="form-control" id="title" placeholder="Title" aria-describedby="emailHelp" />
                            </div>
                            <div className="">
                                <textarea name="content" value={inputVal.content} name="content" onChange={inputFun} className="form-control" placeholder="Content" id="content"></textarea>
                            </div>
                            <Button type="button" onClick={addItem} className="btn btn_Add btn-sm btn-default float-right mt-2"><AddIcon /></Button>
                        </form>
                    </div>
                </div>
            </div>
        </>
    )
}
export default Form;
