import React from 'react';
import Button from '@material-ui/core/Button';
import AddIcon from '@material-ui/icons/Add';
import { useState } from 'react';
import DeleteIcon from '@material-ui/icons/Delete';

const Form = () => {
    const [InputValue, setInputValue] = useState({
        title:"",
        content: ""
    });
    const [NewInputItem, SetNewInputItem] = useState([]);
    
    const inputFun = (event) => {
        const {value, name} = event.target;

        setInputValue((prevValue) => {            
            return{
                ...prevValue,
                [name]: value
            }
        });
    }

    const addItem = () => {        
        SetNewInputItem((oldItem) => {
            return[...oldItem, InputValue]
        })
        setInputValue({
            title:"",
            content: ""
        })
    }

    return(
        <>
            <h1>{InputValue.title}</h1>
            <div className="container">
                <div className="row d-flex justify-content-center">
                    <div className="card col-12 col-md-4 mt-2">
                        <form className="card-body">
                            <div className="mb-3">                
                                <input type="text" value={InputValue.title} name="title" onChange={inputFun} className="form-control" id="title" placeholder="Title" aria-describedby="emailHelp" />
                            </div>
                            <div className="">
                                <textarea onChange={inputFun} value={InputValue.content} name="content" className="form-control" placeholder="Content" id="content"></textarea>
                            </div>
                            <Button type="button" onClick={addItem} className="btn btn_Add btn-sm btn-default float-right mt-2"><AddIcon /></Button>
                        </form>
                    </div>
                </div>
                <div className="row">
                    <div className="col-xs-12 col-sm-6 col-md-4 col-lg-3">
                        <div className="card custom-card mt-4">
                            <div className="card-body">
                                <div className="card-header">
                                    <p className="bold">{NewInputItem.title}</p>
                                </div>
                                <div className="card-text">
                                    <p>{NewInputItem.content}</p>
                                </div>
                                <div className="card-footer">
                                    <Button type="button" className="btn btn_delete btn-sm btn-default float-right"><DeleteIcon /></Button>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </>
    )
}
export default Form;
