import React from "react";
import Button from '@material-ui/core/Button';
import DeleteIcon from '@material-ui/icons/Delete';
const Card = (props) => {
    //const boxShadow = props.background;
    const boxShadow = "lightgrey";
    return(
        <>
            <div className="col-xs-12 col-sm-6 col-md-4 col-lg-3">
                <div className="card custom-card mt-4">
                    <div className="card-body" style={{boxShadow: `2px 2px 2px ${boxShadow};color:(props.colors)`}}>
                        <div className="card-header">
                            <p className="bold">{props.title}</p>
                        </div>
                        <div className="card-text">
                            <p>{props.content}</p>
                        </div>
                        <div className="card-footer">
                            <Button type="button" className="btn btn_delete btn-sm btn-default float-right" onClick={() => {
                                props.onSelect(props.id)
                            }}><DeleteIcon /></Button>
                        </div>
                    </div>
                </div>
            </div>
        </>
    )
}
export default Card;
